import type * as React from "react";

import { cn } from "#shadcn/lib/utils";

// Base card styles
const baseCardStyles = cn(
	// Layout
	"flex",
	"flex-col",
	"gap-6",
	// Borders
	"border",
	"border-border-primary",
	// Background & Colors
	"bg-bg-primary",
	"text-text-primary",
	// Shadow
	"shadow-sm",
);

// Default card styles
const defaultCardStyles = cn(
	baseCardStyles,
	// Shape
	"rounded-xl",
	// Spacing
	"py-6",
);

// Round card styles
const roundCardStyles = cn(
	baseCardStyles,
	// Shape
	"rounded-3xl",
	// Spacing
	"px-8",
	"py-6",
);

// Round white card styles (no border, no shadow)
const roundWhiteCardStyles = cn(
	// Layout
	"flex",
	"flex-col",
	"gap-6",
	// Background & Colors
	"bg-bg-primary",
	"text-text-primary",
	// Shape
	"rounded-3xl",
	// Spacing
	"px-8",
	"py-6",
);

type CardVariant = "default" | "round" | "round-white";

interface CardProps extends React.ComponentProps<"div"> {
	variant?: CardVariant;
}

function Card({ className, variant = "default", ...props }: CardProps) {
	const variantStyles =
		variant === "round"
			? roundCardStyles
			: variant === "round-white"
				? roundWhiteCardStyles
				: defaultCardStyles;

	return (
		<div data-slot="card" className={cn(variantStyles, className)} {...props} />
	);
}

function CardHeader({ className, ...props }: React.ComponentProps<"div">) {
	return (
		<div
			data-slot="card-header"
			className={cn(
				"@container/card-header",
				"grid",
				"auto-rows-min",
				"grid-rows-[auto_auto]",
				"items-start",
				"gap-1.5",
				"px-6",
				"has-data-[slot=card-action]:grid-cols-[1fr_auto]",
				"[.border-b]:pb-6",
				className,
			)}
			{...props}
		/>
	);
}

function CardTitle({ className, ...props }: React.ComponentProps<"div">) {
	return (
		<div
			data-slot="card-title"
			className={cn("leading-none", "font-semibold", className)}
			{...props}
		/>
	);
}

function CardDescription({ className, ...props }: React.ComponentProps<"div">) {
	return (
		<div
			data-slot="card-description"
			className={cn("text-text-secondary", "text-sm", className)}
			{...props}
		/>
	);
}

function CardAction({ className, ...props }: React.ComponentProps<"div">) {
	return (
		<div
			data-slot="card-action"
			className={cn(
				"col-start-2",
				"row-span-2",
				"row-start-1",
				"self-start",
				"justify-self-end",
				className,
			)}
			{...props}
		/>
	);
}

function CardContent({ className, ...props }: React.ComponentProps<"div">) {
	return (
		<div
			data-slot="card-content"
			className={cn("px-6", className)}
			{...props}
		/>
	);
}

function CardFooter({ className, ...props }: React.ComponentProps<"div">) {
	return (
		<div
			data-slot="card-footer"
			className={cn(
				"flex",
				"items-center",
				"px-6",
				"[.border-t]:pt-6",
				className,
			)}
			{...props}
		/>
	);
}

export {
	Card,
	CardHeader,
	CardFooter,
	CardTitle,
	CardAction,
	CardDescription,
	CardContent,
};

// biome-ignore-all lint/a11y/useValidAnchor: Useless in stories
import type { Meta, StoryObj } from "@storybook/react-vite";
import { Button } from "#shadcn/components/ui/button";
import {
	Card,
	CardAction,
	CardContent,
	CardDescription,
	CardFooter,
	CardHeader,
	CardTitle,
} from "#shadcn/components/ui/card";
import { Input } from "#shadcn/components/ui/input";
import { Label } from "#shadcn/components/ui/label";

const meta = {
	title: "Component/Card",
	component: Card,
	parameters: {
		layout: "centered",
	},
	argTypes: {
		variant: {
			control: { type: "select" },
			options: ["default", "round", "round-white"],
			description: "Card variant",
		},
	},
} satisfies Meta<typeof Card>;
export default meta;

type Story = StoryObj<typeof meta>;

export const Demo = {
	args: {
		variant: "default",
	},
	render: ({ variant }) => (
		<Card variant={variant || "default"} className="w-full max-w-sm">
			<CardHeader>
				<CardTitle>Login to your account</CardTitle>
				<CardDescription>
					Enter your email below to login to your account
				</CardDescription>
				<CardAction>
					<Button variant="link">Sign Up</Button>
				</CardAction>
			</CardHeader>
			<CardContent>
				<form>
					<div className="flex flex-col gap-6">
						<div className="grid gap-2">
							<Label htmlFor="login">Login</Label>
							<Input id="login" type="text" placeholder="user" required />
						</div>
						<div className="grid gap-2">
							<div className="flex items-center">
								<Label htmlFor="password">Password</Label>
								<a
									href="#"
									className="ml-auto inline-block text-sm underline-offset-4 hover:underline"
								>
									Forgot your password?
								</a>
							</div>
							<Input id="password" type="password" required />
						</div>
					</div>
				</form>
			</CardContent>
			<CardFooter className="flex-col gap-2">
				<Button type="submit" className="w-full">
					Login
				</Button>
				<Button variant="secondary" className="w-full">
					Login with Google
				</Button>
			</CardFooter>
		</Card>
	),
} satisfies Story;
