import {
	ChevronDownIcon,
	ChevronLeftIcon,
	ChevronRightIcon,
	MoreHorizontalIcon,
} from "lucide-react";
import type * as React from "react";
import { type Button, buttonVariants } from "#shadcn/components/ui/button";
import {
	DropdownMenu,
	DropdownMenuContent,
	DropdownMenuItem,
	DropdownMenuTrigger,
} from "#shadcn/components/ui/dropdown-menu";
import { cn } from "#shadcn/lib/utils";

function Pagination({ className, ...props }: React.ComponentProps<"nav">) {
	return (
		// biome-ignore lint/a11y/useSemanticElements: FIXME: unchanged shadcn
		<nav
			// biome-ignore lint/a11y/noRedundantRoles: FIXME: unchanged shadcn
			role="navigation"
			aria-label="pagination"
			data-slot="pagination"
			className={cn("flex", "justify-center", className)}
			{...props}
		/>
	);
}

function PaginationContent({
	className,
	...props
}: React.ComponentProps<"ul">) {
	return (
		<ul
			data-slot="pagination-content"
			className={cn("flex", "flex-row", "items-center", "gap-1", className)}
			{...props}
		/>
	);
}

function PaginationItem({ ...props }: React.ComponentProps<"li">) {
	return (
		<li
			className={cn("hover:bg-bg-secondary", "rounded-md")}
			data-slot="pagination-item"
			{...props}
		/>
	);
}

type PaginationLinkProps = {
	isActive?: boolean;
} & Pick<React.ComponentProps<typeof Button>, "size"> &
	React.ComponentProps<"a">;

function PaginationLink({
	className,
	isActive,
	size = "small",
	...props
}: PaginationLinkProps) {
	return (
		<a
			aria-current={isActive ? "page" : undefined}
			data-slot="pagination-link"
			data-active={isActive}
			className={cn(
				buttonVariants({
					variant: isActive ? "secondary" : "ghost",
					size,
				}),
				className,
			)}
			{...props}
		/>
	);
}

function PaginationPrevious({
	className,
	...props
}: React.ComponentProps<typeof PaginationLink>) {
	return (
		<PaginationLink
			aria-label="Go to previous page"
			size="small"
			className={cn("gap-1", "px-2.5", "sm:pl-2.5", className)}
			{...props}
		>
			<ChevronLeftIcon />
		</PaginationLink>
	);
}

function PaginationNext({
	className,
	...props
}: React.ComponentProps<typeof PaginationLink>) {
	return (
		<PaginationLink
			aria-label="Go to next page"
			size="regular"
			className={cn("gap-1", "px-2.5", "sm:pr-2.5", className)}
			{...props}
		>
			<ChevronRightIcon />
		</PaginationLink>
	);
}

function PaginationEllipsis({
	className,
	...props
}: React.ComponentProps<"span">) {
	return (
		<span
			aria-hidden
			data-slot="pagination-ellipsis"
			className={cn(
				"flex",
				"size-9",
				"items-center",
				"justify-center",
				className,
			)}
			{...props}
		>
			<MoreHorizontalIcon className="size-4" />
			<span className="sr-only">More pages</span>
		</span>
	);
}

type PaginationPageSizeSelectorProps = {
	pageSize: number;
	onPageSizeChange: (pageSize: number) => void;
	pageSizeOptions?: number[];
	className?: string;
};

function PaginationPageSizeSelector({
	pageSize,
	onPageSizeChange,
	pageSizeOptions = [10, 20, 50, 100],
	className,
}: PaginationPageSizeSelectorProps) {
	return (
		<DropdownMenu>
			<DropdownMenuTrigger
				className={cn(
					"flex",
					"items-center",
					"gap-1",
					"text-sm",
					"text-text-secondary",
					"hover:text-text-primary",
					"outline-none",
					className,
				)}
				data-slot="pagination-page-size-selector"
			>
				{pageSize} / Page
				<ChevronDownIcon className="size-4" />
			</DropdownMenuTrigger>
			<DropdownMenuContent>
				{pageSizeOptions.map((option) => (
					<DropdownMenuItem
						key={option}
						onClick={() => onPageSizeChange(option)}
					>
						{option}
					</DropdownMenuItem>
				))}
			</DropdownMenuContent>
		</DropdownMenu>
	);
}

export {
	Pagination,
	PaginationContent,
	PaginationLink,
	PaginationItem,
	PaginationPrevious,
	PaginationNext,
	PaginationEllipsis,
	PaginationPageSizeSelector,
};

import type { Meta, StoryObj } from "@storybook/react-vite";

import {
	Pagination,
	PaginationContent,
	PaginationEllipsis,
	PaginationItem,
	PaginationLink,
	PaginationNext,
	PaginationPageSizeSelector,
	PaginationPrevious,
} from "#shadcn/components/ui/pagination";

const meta = {
	title: "Component/Pagination",
} satisfies Meta;
export default meta;

type Story = StoryObj<typeof meta>;

export const Demo = {
	render: () => (
		<Pagination>
			<PaginationPageSizeSelector
				pageSize={10}
				onPageSizeChange={(_: number) => {}}
				className="p-2"
			/>
			<PaginationContent>
				<PaginationItem>
					<PaginationPrevious href="#" />
				</PaginationItem>
				<PaginationItem>
					<PaginationLink href="#">1</PaginationLink>
				</PaginationItem>
				<PaginationItem>
					<PaginationLink href="#" isActive>
						2
					</PaginationLink>
				</PaginationItem>
				<PaginationItem>
					<PaginationEllipsis />
				</PaginationItem>
				<PaginationItem>
					<PaginationLink href="#">3</PaginationLink>
				</PaginationItem>
				<PaginationItem>
					<PaginationNext href="#" />
				</PaginationItem>
			</PaginationContent>
		</Pagination>
	),
} satisfies Story;
