"use client";
import * as RadioGroupPrimitive from "@radix-ui/react-radio-group";
import type * as React from "react";

import { cn } from "#shadcn/lib/utils";

function RadioGroup({
	className,
	...props
}: React.ComponentProps<typeof RadioGroupPrimitive.Root>) {
	return (
		<RadioGroupPrimitive.Root
			data-slot="radio-group"
			className={cn("grid", "gap-3", className)}
			{...props}
		/>
	);
}

function RadioGroupItem({
	className,
	...props
}: React.ComponentProps<typeof RadioGroupPrimitive.Item>) {
	return (
		<RadioGroupPrimitive.Item
			data-slot="radio-group-item"
			className={cn(
				// Base styles
				"aspect-square",
				"size-4",
				"shrink-0",
				"rounded-full",
				"overflow-hidden",
				"relative",
				"outline-none",
				"cursor-pointer",
				// Animations
				"transition-all",
				"duration-200",
				"active:scale-90",
				"active:duration-75",
				// Default state:
				"bg-white",
				"border-[1.6px]",
				"border-solid",
				"border-border-primary",
				// Checked state:
				"data-[state=checked]:bg-bg-link",
				"data-[state=checked]:border-bg-link",
				// Disabled states
				"disabled:cursor-not-allowed",
				"disabled:active:scale-100",
				"disabled:bg-white",
				"disabled:border-border-secondary",
				"disabled:data-[state=checked]:bg-border-secondary",
				"disabled:data-[state=checked]:border-border-secondary",
				// Focus styles
				"focus-visible:ring-2",
				"focus-visible:ring-bg-link",
				"focus-visible:ring-offset-2",
				className,
			)}
			{...props}
		>
			<RadioGroupPrimitive.Indicator
				data-slot="radio-group-indicator"
				className={cn(
					"absolute",
					"inset-0",
					"flex",
					"items-center",
					"justify-center",
				)}
			>
				<div className={cn("size-1.5", "rounded-full", "bg-white")} />
			</RadioGroupPrimitive.Indicator>
		</RadioGroupPrimitive.Item>
	);
}

export { RadioGroup, RadioGroupItem };

import type { Meta, StoryObj } from "@storybook/react-vite";
import { Label } from "#shadcn/components/ui/label";
import { RadioGroup, RadioGroupItem } from "#shadcn/components/ui/radio-group";

const meta = {
	title: "Component/Radio group",
} satisfies Meta;
export default meta;

type Story = StoryObj<typeof meta>;

export const Demo = {
	render: () => (
		<div className="space-y-6">
			<RadioGroup defaultValue="checked">
				<div className="flex items-center gap-3">
					<RadioGroupItem value="default" id="r1" />
					<Label htmlFor="r1">Default</Label>
				</div>
				<div className="flex items-center gap-3">
					<RadioGroupItem value="checked" id="r2" />
					<Label htmlFor="r2">Checked</Label>
				</div>
				<div className="flex items-center gap-3">
					<RadioGroupItem value="disabled" id="r3" disabled />
					<Label htmlFor="r3">Disabled</Label>
				</div>
			</RadioGroup>
			<RadioGroup defaultValue="disabled-checked">
				<div className="flex items-center gap-3">
					<RadioGroupItem value="disabled-checked" id="r4" disabled />
					<Label htmlFor="r4">Disabled Checked</Label>
				</div>
			</RadioGroup>
		</div>
	),
} satisfies Story;
