"use client";
import * as ToggleGroupPrimitive from "@radix-ui/react-toggle-group";
import type { VariantProps } from "class-variance-authority";
import * as React from "react";
import { toggleVariants } from "#shadcn/components/ui/toggle";
import { cn } from "#shadcn/lib/utils";

// Toggle group styles
const toggleGroupStyles = cn(
	"group/toggle-group",
	"flex",
	"w-fit",
	"items-center",
	"rounded-md",
	"data-[variant=outline]:shadow-xs",
);

// Toggle group item styles
const toggleGroupItemStyles = cn(
	"min-w-0",
	"flex-1",
	"shrink-0",
	"rounded-none",
	"shadow-none",
	"first:rounded-l-md",
	"last:rounded-r-md",
	"focus:z-10",
	"focus-visible:z-10",
	"data-[variant=outline]:border-l-0",
	"data-[variant=outline]:first:border-l",
);

const ToggleGroupContext = React.createContext<
	VariantProps<typeof toggleVariants>
>({
	size: "default",
	variant: "default",
});

function ToggleGroup({
	className,
	variant,
	size,
	children,
	...props
}: React.ComponentProps<typeof ToggleGroupPrimitive.Root> &
	VariantProps<typeof toggleVariants>) {
	return (
		<ToggleGroupPrimitive.Root
			data-slot="toggle-group"
			data-variant={variant}
			data-size={size}
			className={cn(toggleGroupStyles, className)}
			{...props}
		>
			<ToggleGroupContext.Provider value={{ variant, size }}>
				{children}
			</ToggleGroupContext.Provider>
		</ToggleGroupPrimitive.Root>
	);
}

function ToggleGroupItem({
	className,
	children,
	variant,
	size,
	...props
}: React.ComponentProps<typeof ToggleGroupPrimitive.Item> &
	VariantProps<typeof toggleVariants>) {
	const context = React.useContext(ToggleGroupContext);

	return (
		<ToggleGroupPrimitive.Item
			data-slot="toggle-group-item"
			data-variant={context.variant || variant}
			data-size={context.size || size}
			className={cn(
				toggleVariants({
					variant: context.variant || variant,
					size: context.size || size,
				}),
				toggleGroupItemStyles,
				className,
			)}
			{...props}
		>
			{children}
		</ToggleGroupPrimitive.Item>
	);
}

export { ToggleGroup, ToggleGroupItem };
